import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
// useToast removed (not used in this component)
import { CreditCard, Mail, User, X } from 'lucide-react';

interface GuestCheckoutModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (email: string, name: string) => void;
  plan: 'basic' | 'pro' | 'premium';
  isLoading?: boolean;
}

const planDetails = {
  basic: { name: 'Basic', price: '$9.99', duration: '7 days' },
  pro: { name: 'Pro', price: '$29.99', duration: '30 days' },
  premium: { name: 'Premium', price: '$99.99', duration: '90 days' }
};

export const GuestCheckoutModal: React.FC<GuestCheckoutModalProps> = ({
  isOpen,
  onClose,
  onSubmit,
  plan,
  isLoading = false
}) => {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [errors, setErrors] = useState<{ email?: string; name?: string }>({});
  // toast not used in this modal

  const validateForm = () => {
    const newErrors: { email?: string; name?: string } = {};

    if (!email) {
      newErrors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (!name.trim()) {
      newErrors.name = 'Name is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (validateForm()) {
      onSubmit(email.trim(), name.trim());
    }
  };

  const handleClose = () => {
    if (!isLoading) {
      setEmail('');
      setName('');
      setErrors({});
      onClose();
    }
  };

  const currentPlan = planDetails[plan];

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <div className="flex items-center justify-between">
            <DialogTitle className="flex items-center gap-2">
              <CreditCard className="w-5 h-5 text-primary" />
              Complete Your Purchase
            </DialogTitle>
            {!isLoading && (
              <Button
                variant="ghost"
                size="icon"
                onClick={handleClose}
                className="h-6 w-6 rounded-full"
              >
                <X className="w-4 h-4" />
              </Button>
            )}
          </div>
        </DialogHeader>

        <div className="space-y-6">
          {/* Plan Summary */}
          <div className="bg-gradient-to-r from-primary/5 to-secondary/5 rounded-lg p-4 border border-primary/10">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="font-semibold text-foreground">{currentPlan.name} Plan</h3>
                <p className="text-sm text-muted-foreground">{currentPlan.duration} access</p>
              </div>
              <div className="text-right">
                <div className="text-2xl font-bold text-primary">{currentPlan.price}</div>
                <div className="text-xs text-muted-foreground">one-time payment</div>
              </div>
            </div>
          </div>

          {/* Guest Information Form */}
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="guest-email" className="flex items-center gap-2">
                <Mail className="w-4 h-4" />
                Email Address *
              </Label>
              <Input
                id="guest-email"
                type="email"
                placeholder="your@email.com"
                value={email}
                onChange={(e) => {
                  setEmail(e.target.value);
                  if (errors.email) setErrors({ ...errors, email: undefined });
                }}
                disabled={isLoading}
                className={errors.email ? 'border-destructive' : ''}
              />
              {errors.email && (
                <p className="text-sm text-destructive">{errors.email}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="guest-name" className="flex items-center gap-2">
                <User className="w-4 h-4" />
                Full Name *
              </Label>
              <Input
                id="guest-name"
                type="text"
                placeholder="Your full name"
                value={name}
                onChange={(e) => {
                  setName(e.target.value);
                  if (errors.name) setErrors({ ...errors, name: undefined });
                }}
                disabled={isLoading}
                className={errors.name ? 'border-destructive' : ''}
              />
              {errors.name && (
                <p className="text-sm text-destructive">{errors.name}</p>
              )}
            </div>

            <div className="bg-muted/50 rounded-lg p-3">
              <p className="text-xs text-muted-foreground">
                We'll use this information to send you access details and receipts. 
                No account creation required - you can start using the service immediately after payment.
              </p>
            </div>

            <div className="flex gap-3 pt-2">
              <Button
                type="button"
                variant="outline"
                onClick={handleClose}
                disabled={isLoading}
                className="flex-1"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={isLoading}
                className="flex-1 bg-gradient-primary hover:opacity-90"
              >
                {isLoading ? (
                  <div className="flex items-center gap-2">
                    <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                    Processing...
                  </div>
                ) : (
                  `Continue to Payment`
                )}
              </Button>
            </div>
          </form>

          <div className="text-center text-xs text-muted-foreground border-t border-border pt-4">
            ðŸ”’ Your information is secure and encrypted. One-time payment, no recurring charges.
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { User, ChevronDown, Settings, HelpCircle, CreditCard, LogOut, Menu, X } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Sheet, SheetContent, SheetTrigger } from '@/components/ui/sheet';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';

interface HeaderProps {
  onSignOut?: () => void;
}

const Header: React.FC<HeaderProps> = ({ onSignOut }) => {
  const location = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const navItems = [
    { path: '/dashboard', label: 'Dashboard', active: true },
    { path: '/ai-formula', label: 'AI Formula' },
    { path: '/program-ai', label: 'Program AI' },
    { path: '/saved-properties', label: 'Saved Properties' },
    { path: '/market-intel', label: 'Market Intel' },
    { path: '/offers-made', label: 'Offers Made' },
    { path: '/profile', label: 'Profile' }
  ];

  const isActive = (path: string) => {
    return location.pathname === path;
  };

  return (
    <header className="fixed top-0 left-0 right-0 z-50 glass border-b border-white/10">
      <div className="flex items-center justify-between px-6 py-4">
        {/* Logo */}
        <div className="flex items-center space-x-2">
          <img src="/lovable-uploads/10c9e4a0-b0e6-4896-884c-68dde07278eb.png" alt="Apartment Locator AI Logo" className="h-8 w-auto" />
          <h1 className="text-xl font-bold gradient-text">Apartment Locator AI</h1>
        </div>

        {/* Desktop Navigation */}
        <nav className="hidden md:flex items-center space-x-8">
          {navItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`px-3 py-2 rounded-lg font-medium transition-all duration-200 ${
                isActive(item.path)
                  ? 'bg-primary/20 text-primary border border-primary/30'
                  : 'text-muted-foreground hover:text-foreground hover:bg-accent/50'
              }`}
            >
              {item.label}
            </Link>
          ))}
        </nav>

        {/* Mobile Navigation */}
        <Sheet open={mobileMenuOpen} onOpenChange={setMobileMenuOpen}>
          <SheetTrigger asChild className="md:hidden">
            <Button variant="ghost" size="sm">
              <Menu size={20} />
            </Button>
          </SheetTrigger>
          <SheetContent side="right" className="w-72">
            <div className="flex flex-col space-y-4 mt-8">
              {navItems.map((item) => (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`px-3 py-2 rounded-lg font-medium transition-all duration-200 ${
                    isActive(item.path)
                      ? 'bg-primary/20 text-primary border border-primary/30'
                      : 'text-muted-foreground hover:text-foreground hover:bg-accent/50'
                  }`}
                  onClick={() => setMobileMenuOpen(false)}
                >
                  {item.label}
                </Link>
              ))}
              <div className="border-t border-border pt-4 space-y-2">
                <Link
                  to="/billing"
                  className="block px-3 py-2 text-muted-foreground hover:text-foreground"
                  onClick={() => setMobileMenuOpen(false)}
                >
                  Billing & Plans
                </Link>
                <Link
                  to="/help"
                  className="block px-3 py-2 text-muted-foreground hover:text-foreground"
                  onClick={() => setMobileMenuOpen(false)}
                >
                  Help & Support
                </Link>
                <Link
                  to="/data-export"
                  className="block px-3 py-2 text-muted-foreground hover:text-foreground"
                  onClick={() => setMobileMenuOpen(false)}
                >
                  Export My Data
                </Link>
                <Link
                  to="/contact"
                  className="block px-3 py-2 text-muted-foreground hover:text-foreground"
                  onClick={() => setMobileMenuOpen(false)}
                >
                  Contact Support
                </Link>
                {onSignOut && (
                  <button
                    onClick={() => {
                      onSignOut();
                      setMobileMenuOpen(false);
                    }}
                    className="block w-full text-left px-3 py-2 text-red-600 hover:bg-red-50"
                  >
                    Sign Out
                  </button>
                )}
              </div>
            </div>
          </SheetContent>
        </Sheet>

        {/* User Section */}
        <div className="flex items-center space-x-4">
          {/* Free Plan Badge */}
          <div className="hidden sm:flex items-center space-x-2 px-3 py-1 rounded-full bg-secondary/20 border border-secondary/30">
            <div className="w-2 h-2 bg-secondary rounded-full"></div>
            <span className="text-sm font-medium text-secondary">Free Plan</span>
          </div>

          {/* User Menu */}
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" className="flex items-center gap-2 px-2">
                <div className="w-8 h-8 rounded-full bg-gradient-primary flex items-center justify-center">
                  <User size={16} className="text-primary-foreground" />
                </div>
                <ChevronDown size={14} className="text-muted-foreground" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end" className="w-48">
              <DropdownMenuItem asChild>
                <Link to="/billing" className="flex items-center gap-2">
                  <CreditCard size={16} />
                  Billing & Plans
                </Link>
              </DropdownMenuItem>
              <DropdownMenuItem asChild>
                <Link to="/help" className="flex items-center gap-2">
                  <HelpCircle size={16} />
                  Help & Support
                </Link>
              </DropdownMenuItem>
              <DropdownMenuItem asChild>
                <Link to="/data-export" className="flex items-center gap-2">
                  <Settings size={16} />
                  Export My Data
                </Link>
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              {onSignOut && (
                <DropdownMenuItem onClick={onSignOut} className="flex items-center gap-2 text-red-600">
                  <LogOut size={16} />
                  Sign Out
                </DropdownMenuItem>
              )}
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </header>
  );
};

export default Header;

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { TrendingUp, Filter, ArrowUpDown } from 'lucide-react';
import { Button } from '@/components/ui/button';
// badge removed (unused)
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
// progress removed (unused)
import { PointOfInterest, SmartProperty } from '@/hooks/useLocationIntelligence';
import ModernApartmentCard from '@/components/modern/ModernApartmentCard';

interface SmartResultsProps {
  smartResults: SmartProperty[];
  pointsOfInterest: PointOfInterest[];
  userProfile: Record<string, unknown> | null;
  getCombinedScore: (propertyId: string) => number;
  onPropertySelect?: (id: string) => void;
  selectedPropertyId?: string | null;
}

const SmartResults: React.FC<SmartResultsProps> = ({
  smartResults,
  pointsOfInterest,
  userProfile,
  getCombinedScore,
  onPropertySelect,
  selectedPropertyId
}) => {
  const navigate = useNavigate();
  const [sortBy, setSortBy] = useState<'combinedScore' | 'locationScore' | 'price'>('combinedScore');
  const [filterBy, setFilterBy] = useState<'all' | 'topPicks' | 'budgetMatch'>('all');
  const [hoveredProperty, setHoveredProperty] = useState<string | null>(null);

  const sortedResults = [...smartResults].sort((a, b) => {
    // Always prioritize AI Top Picks first
    if (a.isTopPick && !b.isTopPick) return -1;
    if (!a.isTopPick && b.isTopPick) return 1;

    // Then sort by selected criteria
    switch (sortBy) {
      case 'combinedScore':
        return b.combinedScore - a.combinedScore;
      case 'locationScore':
        return b.locationScore - a.locationScore;
      case 'price':
        return a.price - b.price;
      default:
        return 0;
    }
  });

  const filteredResults = sortedResults.filter(property => {
    switch (filterBy) {
      case 'topPicks':
        return property.isTopPick;
      case 'budgetMatch':
        return property.budgetMatch;
      default:
        return true;
    }
  });

  const getScoreColor = (score: number) => {
    if (score >= 90) return 'text-green-400';
    if (score >= 80) return 'text-yellow-400';
    if (score >= 70) return 'text-orange-400';
    return 'text-red-400';
  };

  const getScoreBg = (score: number) => {
    if (score >= 90) return 'bg-green-500/20 border-green-500/30';
    if (score >= 80) return 'bg-yellow-500/20 border-yellow-500/30';
    if (score >= 70) return 'bg-orange-500/20 border-orange-500/30';
    return 'bg-red-500/20 border-red-500/30';
  };

  const isArray = (v: unknown): v is unknown[] => Array.isArray(v);

  const getPreferencesCount = () => {
    let count = 0;
    if (userProfile && userProfile['budget']) count++;
    if (userProfile && isArray(userProfile['amenities']) && userProfile['amenities'].length > 0) count++;
    if (userProfile && userProfile['lifestyle']) count++;
    if (userProfile && isArray(userProfile['priorities']) && userProfile['priorities'].length > 0) count++;
    return count;
  };

  return (
    <div className="space-y-6">
      {/* Results Header with Controls */}
      <Card className="bg-slate-800/30 border border-slate-700/30">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle className="flex items-center gap-2 text-xl">
                <TrendingUp className="w-6 h-6 text-blue-400" />
                Smart Results
              </CardTitle>
              <p className="text-muted-foreground mt-1">
                {filteredResults.length} properties ranked by AI + location scoring
              </p>
            </div>

            <div className="flex items-center gap-3">
              <Select value={sortBy} onValueChange={(value: string) => setSortBy(value as 'combinedScore' | 'locationScore' | 'price')}>
                <SelectTrigger className="w-48 bg-slate-700/50 border-slate-600/50">
                  <ArrowUpDown className="w-4 h-4 mr-2" />
                  <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-slate-800 border-slate-600">
                  <SelectItem value="combinedScore">Combined AI Score</SelectItem>
                  <SelectItem value="locationScore">Location Score</SelectItem>
                  <SelectItem value="price">Price (Low to High)</SelectItem>
                </SelectContent>
              </Select>

              <Select value={filterBy} onValueChange={(value: string) => setFilterBy(value as 'all' | 'topPicks' | 'budgetMatch')}>
                <SelectTrigger className="w-36 bg-slate-700/50 border-slate-600/50">
                  <Filter className="w-4 h-4 mr-2" />
                  <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-slate-800 border-slate-600">
                  <SelectItem value="all">All Results</SelectItem>
                  <SelectItem value="topPicks">Top Picks</SelectItem>
                  <SelectItem value="budgetMatch">Budget Match</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardHeader>
      </Card>

      {/* Property Results */}
      <div className="space-y-6 w-full">
        {filteredResults.map((property) => (
          <ModernApartmentCard
            key={property.id}
            apartment={{
              id: property.id,
              name: property.name,
              address: property.address,
              price: property.price,
              aiMatchScore: property.aiMatchScore,
              bedrooms: property.bedrooms,
              bathrooms: property.bathrooms,
              sqft: property.sqft,
              isTopPick: property.isTopPick,
              budgetMatch: property.budgetMatch,
              amenityMatch: property.amenityMatch,
              lifestyleMatch: property.lifestyleMatch,
              locationScore: property.locationScore,
              combinedScore: property.combinedScore,
              savings: property.savings,
              walkScore: 85,
              transitScore: 78,
              bikeScore: 82,
              yearBuilt: 2020,
              petPolicy: "Dogs & Cats Welcome",
              parking: "Covered Garage",
              utilities: ["Water", "Trash", "Internet"],
              marketAverage: property.price + (property.savings || 0),
              apartmentIQData: {
                currentRent: property.price,
                originalRent: property.price + 400,
                concessions: [
                  {
                    type: "free_months",
                    value: 2,
                    description: "2 months free on 12-month lease"
                  }
                ],
                negotiationPotential: {
                  score: 9,
                  factors: ["High vacancy rate", "Long listing time", "Seasonal market"]
                },
                landlordLossIndicator: {
                  days30: 2400,
                  days60: 4800,
                  days90: 7200
                }
              },
              poiDistances: property.poiTimes.reduce((acc: Record<string, {distance: string; driveTime: number}>, poi) => {
                acc[poi.poiName] = {
                  distance: `${(poi.time * 0.5).toFixed(1)} mi`,
                  driveTime: poi.time
                };
                return acc;
              }, {} as Record<string, {distance: string; driveTime: number}>)
            }}
            pointsOfInterest={pointsOfInterest}
            onSave={(id) => console.log('Save:', id)}
            onViewDetails={(id) => {
              console.log('Navigating to property details:', id);
              navigate(`/property/${id}`);
            }}
            onMakeOffer={(id) => {
              console.log('Navigating to make offer:', id);
              navigate(`/generate-offer?property=${id}`);
            }}
            saved={false}
          />
        ))}
      </div>
    </div>
  );
};

export default SmartResults;

import React from 'react';
import { Badge } from './ui/badge';

interface PricingBreakdownProps {
  originalPrice: number;
  aiPrice: number;
  effectivePrice: number;
  concessions: number;
  successRate: number;
  monthlySavings: number;
}

const PricingBreakdown: React.FC<PricingBreakdownProps> = ({
  originalPrice,
  aiPrice,
  effectivePrice,
  concessions,
  successRate,
  monthlySavings
}) => {
  // Calculation Logic:
  // Line 1: Original - AI Predicted = Potential Savings
  const potentialSavings = originalPrice - aiPrice;
  // Line 2: Concession Value = Additional Savings
  const additionalSavings = concessions;
  // Line 3: Total Monthly (Potential + Additional), Annual (Total Monthly Ã— 12)
  const totalMonthlySavings = potentialSavings + additionalSavings;
  const totalAnnualSavings = totalMonthlySavings * 12;
  return (
    <div className="space-y-4">
      {/* Visual Card Layout */}
      <div className="bg-slate-900/60 border border-slate-700/50 rounded-2xl p-6 backdrop-blur-sm">
        {/* Original Price */}
        <div className="text-sm text-slate-400 line-through mb-3">
          ${originalPrice.toLocaleString()}/mo listed price
        </div>

        {/* AI Recommended Offer */}
        <div className="mb-4">
          <div className="text-3xl font-bold text-blue-400 mb-2">
            ${aiPrice.toLocaleString()}/mo
          </div>
          <div className="flex items-center gap-3">
            <span className="text-sm text-slate-300">AI Recommended Offer</span>
            <Badge 
              variant="secondary" 
              className="bg-gradient-to-r from-blue-500/20 to-purple-500/20 text-blue-300 border-blue-500/30 text-xs"
            >
              {successRate}% Success
            </Badge>
          </div>
        </div>

        {/* Divider */}
        <div className="border-t border-slate-700/50 my-4"></div>

        {/* Expected Concessions */}
        <div className="mb-4">
          <div className="text-sm text-teal-400 font-medium mb-1">
            + Expected Concessions
          </div>
          <div className="text-xl font-bold text-teal-400">
            ${concessions} value
          </div>
        </div>

        {/* Final Effective Cost */}
        <div className="bg-teal-500/10 border border-teal-500/30 rounded-xl p-4">
          <div className="text-2xl font-bold text-teal-400 mb-1">
            ${effectivePrice.toLocaleString()}/mo
          </div>
          <div className="text-sm text-slate-300">
            Your effective monthly cost{' '}
            <span className="text-teal-400 font-medium">
              (Save ${totalMonthlySavings.toLocaleString()}/mo)
            </span>
          </div>
        </div>
      </div>

      {/* Price Breakdown */}
      <div className="bg-slate-900/60 border border-slate-700/50 rounded-2xl p-6 backdrop-blur-sm">
        <h3 className="text-lg font-bold text-foreground mb-4 text-center">
          Price Breakdown
        </h3>

        <div className="space-y-3">
          {/* Listed Price */}
          <div className="flex justify-between items-center py-2">
            <span className="text-sm text-slate-300">Listed Price</span>
            <span className="text-sm text-slate-400 line-through">
              ${originalPrice.toLocaleString()}/mo
            </span>
          </div>

          {/* AI Recommended Offer */}
          <div className="flex justify-between items-center py-2">
            <span className="text-sm text-slate-300">AI Recommended Offer</span>
            <span className="text-lg font-bold text-blue-400">
              ${aiPrice.toLocaleString()}/mo
            </span>
          </div>

          {/* Concessions Value */}
          <div className="flex justify-between items-center py-2">
            <span className="text-sm text-slate-300">Monthly Concessions</span>
            <span className="text-lg font-bold text-teal-400">
              ${concessions}/mo
            </span>
          </div>

          {/* Divider */}
          <div className="border-t border-slate-700/50 my-3"></div>

          {/* Your Effective Cost */}
          <div className="flex justify-between items-center py-2 bg-teal-500/5 rounded-lg px-3 border border-teal-500/20">
            <span className="text-base font-medium text-foreground">Your Effective Cost</span>
            <span className="text-xl font-bold text-teal-400">
              ${effectivePrice.toLocaleString()}/mo
            </span>
          </div>
        </div>

        {/* Summary note */}
        <div className="text-xs text-slate-400 text-center mt-4">
          Line 1: ${potentialSavings.toLocaleString()} â€¢ Line 2: ${additionalSavings.toLocaleString()} â€¢ Line 3: ${totalMonthlySavings.toLocaleString()}/mo (${totalAnnualSavings.toLocaleString()}/yr)
        </div>
      </div>
    </div>
  );
};

export default PricingBreakdown;

import React from 'react';
import { Search, TrendingUp, Settings, Target } from 'lucide-react';

interface PointOfInterestShort {
  id: string;
  name: string;
  address?: string;
  maxTime?: number;
  transportMode?: 'driving' | 'transit' | 'walking' | 'biking';
}

interface SyncedSettings {
  location: string;
  radius: number;
  maxDriveTime: number;
  pointsOfInterest: PointOfInterestShort[];
}

interface QuickActionsProps {
  onSearchAreaClick?: () => void;
  syncedSettings?: SyncedSettings;
