                  {apartment.marketFactors.seasonalAdvantage && (
                    <div className="flex items-center text-sm text-green-400">
                      <div className="w-2 h-2 bg-green-400 rounded-full mr-2"></div>
                      Seasonal advantage
                    </div>
                  )}
                  {apartment.marketFactors.propertyPressure && (
                    <div className="flex items-center text-sm text-green-400">
                      <div className="w-2 h-2 bg-green-400 rounded-full mr-2"></div>
