import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useNavigate } from 'react-router-dom';
import { OnboardingStep, OnboardingFlowData, defaultFlowData, defaultSteps, OnboardingFlowContextType } from './onboarding-flow-utils'
import { OnboardingFlowContext } from './onboarding-flow-context'

// use defaults from onboarding-flow-utils

// The context object is provided from `onboarding-flow-context.ts` to keep
// this file exporting only the provider component for fast-refresh safety.

interface OnboardingFlowProviderProps {
  children: ReactNode;
}

export const OnboardingFlowProvider: React.FC<OnboardingFlowProviderProps> = ({ children }) => {
  const navigate = useNavigate();

  // Load from localStorage if available
  const [currentStep, setCurrentStep] = useState(() => {
    const saved = localStorage.getItem('onboardingCurrentStep');
    return saved ? parseInt(saved, 10) : 0;
  });

  const [flowData, setFlowData] = useState<OnboardingFlowData>(() => {
    const saved = localStorage.getItem('onboardingFlowData');
    return saved ? { ...defaultFlowData, ...JSON.parse(saved) } : defaultFlowData;
  });

  const [steps, setSteps] = useState<OnboardingStep[]>(() => {
    const saved = localStorage.getItem('onboardingSteps');
    return saved ? JSON.parse(saved) : defaultSteps;
  });

  const totalSteps = steps.length;
  const isComplete = steps.every(step => step.completed || !step.required);

  // Auto-save to localStorage
  useEffect(() => {
    localStorage.setItem('onboardingCurrentStep', currentStep.toString());
  }, [currentStep]);

  useEffect(() => {
    localStorage.setItem('onboardingFlowData', JSON.stringify(flowData));
  }, [flowData]);

  useEffect(() => {
    localStorage.setItem('onboardingSteps', JSON.stringify(steps));
  }, [steps]);

  // Check if current step can proceed
  const canProceed = (() => {
    const currentStepData = steps[currentStep];
    if (!currentStepData) return false;

    switch (currentStepData.id) {
      case 'location':
        return flowData.location.length > 0;
      case 'points-of-interest':
        return flowData.pointsOfInterest.length >= 1; // At least one POI
      case 'budget-housing':
        return flowData.budget > 0 && flowData.bedrooms.length > 0;
      case 'preferences':
        return true; // Optional step
      default:
        return true;
    }
  })();

  const nextStep = () => {
    if (currentStep < totalSteps - 1 && canProceed) {
      markStepComplete(steps[currentStep].id);
      setCurrentStep(prev => prev + 1);
    }
  };

  const previousStep = () => {
    if (currentStep > 0) {
      setCurrentStep(prev => prev - 1);
    }
  };

  const goToStep = (stepIndex: number) => {
    if (stepIndex >= 0 && stepIndex < totalSteps) {
      setCurrentStep(stepIndex);
    }
  };

  const markStepComplete = (stepId: string) => {
    setSteps(prevSteps => 
      prevSteps.map(step => 
        step.id === stepId ? { ...step, completed: true } : step
      )
    );
  };

  const completeOnboarding = () => {
    // Mark all required steps as complete
    setSteps(prevSteps => 
      prevSteps.map(step => ({ ...step, completed: true }))
    );

    // Clear localStorage for onboarding
    localStorage.removeItem('onboardingCurrentStep');
    localStorage.removeItem('onboardingSteps');

    // Navigate to dashboard
    navigate('/dashboard');
  };

  const updateFlowData = (data: Partial<OnboardingFlowData>) => {
    setFlowData(prev => ({ ...prev, ...data }));
  };

  const resetFlow = () => {
    setCurrentStep(0);
    setFlowData(defaultFlowData);
    setSteps(defaultSteps);
    localStorage.removeItem('onboardingCurrentStep');
    localStorage.removeItem('onboardingFlowData');
    localStorage.removeItem('onboardingSteps');
  };

  const value: OnboardingFlowContextType = {
    currentStep,
    totalSteps,
    isComplete,
    flowData,
    nextStep,
    previousStep,
    goToStep,
    completeOnboarding,
    updateFlowData,
    resetFlow,
    steps,
    canProceed,
    markStepComplete
  };

  return (
    <OnboardingFlowContext.Provider value={value}>
      {children}
    </OnboardingFlowContext.Provider>
  );
};

import React, { useState, useEffect, useCallback } from 'react'
import { Property } from '@/data/mockData'
import { toast } from 'sonner'
import { defaultSearchFilters, defaultUserPreferences, safeParseJSON, SearchFilters, UserPreferences } from './property-state-utils'
import { PartialOfferFormData } from '@/data/OfferFormTypes'
import { PropertyStateContext } from './property-state-context'
import { PropertyStateContextType } from './PropertyStateContextTypes'

// Component-only entrypoint to preserve fast-refresh behavior during development.
// For hooks, import from '@/contexts' or '@/contexts/PropertyState' instead.

export const PropertyStateProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [selectedProperty, setSelectedProperty] = useState<Property | null>(null);
  const [offerFormData, setOfferFormData] = useState<PartialOfferFormData>({});
  const [favoriteProperties, setFavoriteProperties] = useState<string[]>([]);
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);

  const [searchFilters, setSearchFiltersState] = useState<SearchFilters>(() =>
    safeParseJSON<SearchFilters>('apartmentiq-filters', defaultSearchFilters)
  )

  const [userPreferences, setUserPreferencesState] = useState<UserPreferences>(() =>
    safeParseJSON<UserPreferences>('apartmentiq-preferences', defaultUserPreferences)
  )

  useEffect(() => {
    const savedFavorites = safeParseJSON('apartmentiq-favorites', [] as string[])
    setFavoriteProperties(savedFavorites)
  }, [])

  useEffect(() => {
    try {
      localStorage.setItem('apartmentiq-favorites', JSON.stringify(favoriteProperties))
    } catch (error) {
      console.error('Error saving favorites to localStorage:', error)
      toast.error('Failed to save favorites')
    }
  }, [favoriteProperties])

  useEffect(() => {
    try {
      localStorage.setItem('apartmentiq-preferences', JSON.stringify(userPreferences))
    } catch (error) {
      console.error('Error saving preferences to localStorage:', error)
      toast.error('Failed to save preferences')
    }
  }, [userPreferences])

  useEffect(() => {
    try {
      localStorage.setItem('apartmentiq-filters', JSON.stringify(searchFilters))
    } catch (error) {
      console.error('Error saving filters to localStorage:', error)
      toast.error('Failed to save search filters')
    }
  }, [searchFilters])

  const setSearchFilters = useCallback((filters: Partial<SearchFilters>) => {
    setSearchFiltersState((prev) => ({ ...prev, ...filters }))
    setHasUnsavedChanges(true)
  }, [])

  const setUserPreferences = useCallback((preferences: Partial<UserPreferences>) => {
    setUserPreferencesState((prev) => ({
      ...prev,
      ...preferences,
      lastActiveDate: new Date().toISOString(),
    }))
    setHasUnsavedChanges(true)
  }, [])

  const toggleFavorite = useCallback((propertyId: string) => {
    setFavoriteProperties(prev => {
      const isFavorited = prev.includes(propertyId)
      const newFavorites = isFavorited ? prev.filter(id => id !== propertyId) : [...prev, propertyId]
      toast.success(isFavorited ? 'Removed from favorites' : 'Added to favorites')
      return newFavorites
    })
  }, [])

  const clearOfferFormData = useCallback(() => setOfferFormData({}), [])

  const resetSearchFilters = useCallback(() => setSearchFiltersState(defaultSearchFilters), [])

  const clearAllData = useCallback(() => {
    setSelectedProperty(null)
    setOfferFormData({})
    setFavoriteProperties([])
    setSearchFiltersState(defaultSearchFilters)
    setUserPreferencesState(defaultUserPreferences)
    setHasUnsavedChanges(false)

    localStorage.removeItem('apartmentiq-favorites')
    localStorage.removeItem('apartmentiq-preferences')
    localStorage.removeItem('apartmentiq-filters')

    toast.success('All data cleared')
  }, [])

  return (
    <PropertyStateContext.Provider
      value={{
        selectedProperty,
        setSelectedProperty,
        favoriteProperties,
        setFavoriteProperties,
        toggleFavorite,
        offerFormData,
        setOfferFormData,
        clearOfferFormData,
        searchFilters,
        setSearchFilters,
        resetSearchFilters,
        userPreferences,
        setUserPreferences,
        clearAllData,
        hasUnsavedChanges,
        setHasUnsavedChanges,
      }}
    >
      {children}
    </PropertyStateContext.Provider>
  )
};

import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

import { useState, useEffect } from 'react';

export const useAIScanning = () => {
  const [propertiesScanned, setPropertiesScanned] = useState(595);
  const [isScanning, setIsScanning] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setPropertiesScanned(prev => prev + Math.floor(Math.random() * 3) + 1);
    }, 15000); // Update every 15 seconds

    return () => clearInterval(interval);
  }, []);

  return { propertiesScanned, isScanning };
};

