                            <FormItem>
                              <FormLabel className="text-sm font-medium text-foreground">Rental History</FormLabel>
                              <FormControl>
                                <Input 
                                  placeholder="No prior rental history" 
                                  {...field} 
                                  className="glass border-white/10 focus:border-primary text-foreground"
