import ModernCard from "@/components/modern/ModernCard";

const NotFound = () => {
  const location = useLocation();

  useEffect(() => {
    console.error(
      "404 Error: User attempted to access non-existent route:",
      location.pathname
    );
  }, [location.pathname]);

  const quickLinks = [
    {
      icon: Home,
      title: "Homepage",
      description: "Return to our main page",
      path: "/",
      color: "text-blue-600"
