    {
      icon: Search,
      title: "Property Search",
      description: "Find apartments with AI",
      path: "/dashboard",
      color: "text-green-600"
    },
    {
      icon: HelpCircle,
      title: "Help Center",
      description: "Get support and answers",
      path: "/help",
      color: "text-purple-600"
    }
