interface AIPreferences {
  // Housing
  bedrooms: string;
  amenities: string[];
  dealBreakers: string[];
  
  // Transportation & Mobility
  publicTransitAccess: string[];
  walkabilityScoreRequirement: string;
  bikeFriendly: boolean;
  evChargingStations: boolean;
  rideShareAvailability: string;
  airportProximity: string;
  highwayAccess: boolean;
  
  // Neighborhood & Community
  schoolDistrictQuality: string;
  crimeRatePreference: string;
  noiseLevelTolerance: string;
  populationDensity: string;
  ageDemographics: string;
  diversityIndex: string;
  localCultureArts: boolean;
  
  // Safety & Security
  securitySystemRequired: boolean;
  gatedCommunityPreference: string;
  emergencyServicesResponseTime: string;
  floodZoneAvoidance: boolean;
  fireSafetyFeatures: string[];
  
  // Shopping & Services
  groceryStoreTypes: string[];
  shoppingMallAccess: boolean;
  farmersMarkets: boolean;
  bankingAccess: boolean;
  postOfficeProximity: boolean;
  dryCleaningServices: boolean;
  
  // Technology & Connectivity
  internetSpeedRequirement: string;
  cellTowerCoverage: string;
  smartHomeCompatibility: boolean;
  cableStreamingOptions?: string[];
  
  // Lifestyle & Preferences
  lifestyle: string;
  workSchedule: string;
  priorities: string[];
  
  // Additional Info
  bio: string;
  useCase: string;
  additionalNotes: string;
}

const ProgramAI = () => {
  const navigate = useNavigate();
  const { searchFilters, setSearchFilters, userPreferences, setUserPreferences } = usePropertyState();
  const [saving, setSaving] = useState(false);
  const [searchSettings, setSearchSettings] = useState<SearchSettings | null>(null);
  
  const [preferences, setPreferences] = useState<AIPreferences>({
    bedrooms: '1',
    amenities: searchFilters.amenities || [],
    dealBreakers: [],
    
    // Transportation & Mobility
    publicTransitAccess: [],
    walkabilityScoreRequirement: 'moderate',
    bikeFriendly: false,
    evChargingStations: false,
    rideShareAvailability: 'standard',
    airportProximity: 'moderate',
    highwayAccess: false,
    
    // Neighborhood & Community
    schoolDistrictQuality: 'no-preference',
    crimeRatePreference: 'low',
    noiseLevelTolerance: 'moderate',
    populationDensity: 'moderate',
    ageDemographics: 'mixed',
    diversityIndex: 'moderate',
    localCultureArts: false,
    
    // Safety & Security
    securitySystemRequired: false,
    gatedCommunityPreference: 'no-preference',
    emergencyServicesResponseTime: 'standard',
    floodZoneAvoidance: false,
    fireSafetyFeatures: [],
    
    // Shopping & Services
    groceryStoreTypes: [],
    shoppingMallAccess: false,
    farmersMarkets: false,
    bankingAccess: false,
    postOfficeProximity: false,
    dryCleaningServices: false,
    
    // Technology & Connectivity
    internetSpeedRequirement: 'standard',
    cellTowerCoverage: 'good',
    smartHomeCompatibility: false,
    cableStreamingOptions: [],
    
    lifestyle: '',
    workSchedule: '',
    priorities: [],
    bio: '',
    useCase: '',
    additionalNotes: ''
  });

  // Load user profile data on component mount
  useEffect(() => {
    const loadUserProfile = async () => {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (!user) return;

        const { data: profile, error } = await supabase
          .from('user_preferences')
          .select('*')
          .eq('user_id', user.id)
          .single();

        if (error && error.code !== 'PGRST116') {
          console.error('Error loading profile:', error);
          return;
        }

        if (profile) {
          const profileRecord = profile as Record<string, unknown>;
          setPreferences(prev => ({
            ...prev,
            bedrooms: (profileRecord.bedrooms as string) || '1',
            amenities: (profileRecord.amenities as string[]) || [],
            dealBreakers: (profileRecord.deal_breakers as string[]) || [],
            
            // Load additional preferences from profile if they exist
            publicTransitAccess: (profileRecord.public_transit_access as string[]) || [],
            walkabilityScoreRequirement: (profileRecord.walkability_score_requirement as string) || 'moderate',
            bikeFriendly: (profileRecord.bike_friendly as boolean) || false,
            evChargingStations: (profileRecord.ev_charging_stations as boolean) || false,
            rideShareAvailability: (profileRecord.ride_share_availability as string) || 'standard',
            airportProximity: (profileRecord.airport_proximity as string) || 'moderate',
            highwayAccess: (profileRecord.highway_access as boolean) || false,
            
            schoolDistrictQuality: (profileRecord.school_district_quality as string) || 'no-preference',
            crimeRatePreference: (profileRecord.crime_rate_preference as string) || 'low',
            noiseLevelTolerance: (profileRecord.noise_level_tolerance as string) || 'moderate',
            populationDensity: (profileRecord.population_density as string) || 'moderate',
            ageDemographics: (profileRecord.age_demographics as string) || 'mixed',
            diversityIndex: (profileRecord.diversity_index as string) || 'moderate',
            localCultureArts: (profileRecord.local_culture_arts as boolean) || false,
            
            securitySystemRequired: (profileRecord.security_system_required as boolean) || false,
            gatedCommunityPreference: (profileRecord.gated_community_preference as string) || 'no-preference',
            emergencyServicesResponseTime: (profileRecord.emergency_services_response_time as string) || 'standard',
            floodZoneAvoidance: (profileRecord.flood_zone_avoidance as boolean) || false,
            fireSafetyFeatures: (profileRecord.fire_safety_features as string[]) || [],
            
            groceryStoreTypes: (profileRecord.grocery_store_types as string[]) || [],
            shoppingMallAccess: (profileRecord.shopping_mall_access as boolean) || false,
            farmersMarkets: (profileRecord.farmers_markets as boolean) || false,
            bankingAccess: (profileRecord.banking_access as boolean) || false,
            postOfficeProximity: (profileRecord.post_office_proximity as boolean) || false,
            dryCleaningServices: (profileRecord.dry_cleaning_services as boolean) || false,
            
            internetSpeedRequirement: (profileRecord.internet_speed_requirement as string) || 'standard',
            cellTowerCoverage: (profileRecord.cell_tower_coverage as string) || 'good',
            smartHomeCompatibility: (profileRecord.smart_home_compatibility as boolean) || false,
            cableStreamingOptions: (profileRecord.cable_streaming_options as string[]) || [],
            
            lifestyle: (profileRecord.lifestyle as string) || '',
            workSchedule: (profileRecord.work_schedule as string) || '',
            priorities: (profileRecord.priorities as string[]) || [],
            bio: (profileRecord.bio as string) || '',
            useCase: (profileRecord.use_case as string) || '',
            additionalNotes: (profileRecord.additional_notes as string) || ''
          }));
        }
      } catch (error) {
        console.error('Error loading user profile:', error);
      }
    };

    loadUserProfile();
    
    // Track page visit
    dataTracker.trackPageView();
    dataTracker.trackContent({
      contentType: 'ai_preferences',
      action: 'view',
      contentData: { step: 'initial_load' }
    });
  }, []);

  const commonAmenities = [
    'Pool', 'Gym/Fitness Center', 'Parking', 'Laundry', 'Pet-Friendly',
    'Balcony/Patio', 'In-Unit Washer/Dryer', 'Air Conditioning', 'Dishwasher',
    'Walk-in Closet', 'Rooftop Access', 'Concierge', 'Storage'
  ];

  const commonDealBreakers = [
    'No Parking', 'No Pets', 'Ground Floor', 'No AC', 'Shared Laundry',
    'No Dishwasher', 'Street Parking Only', 'No Elevator', 'Carpet Floors',
    'No Balcony', 'High Traffic Area', 'Far from Transit'
  ];

  const priorityOptions = [
    'Short Commute', 'Budget-Friendly', 'Modern Amenities', 'Pet-Friendly',
    'Nightlife Access', 'Family-Friendly', 'Transit Access', 'Quiet Area',
    'Walkable Neighborhood', 'Parking Available'
  ];

  const updatePreference = (key: keyof AIPreferences, value: unknown) => {
    // Use a helper to avoid explicit `any` while allowing unknown runtime values
    setPreferences(prev => ({ ...prev, [key]: value as unknown as AIPreferences[keyof AIPreferences] }));
    
    // Track preference changes
      dataTracker.trackContent({
      contentType: 'ai_preferences',
      action: 'update',
      contentData: {
        preference_key: key,
        old_value: preferences[key],
        // new_value can be runtime-unknown; cast to Json for tracking payload
        new_value: value as unknown as Json,
        timestamp: new Date().toISOString()
      }
    });
  };

  const toggleArrayItem = (array: string[], item: string) => {
    const newArray = array.includes(item) 
      ? array.filter(i => i !== item)
      : [...array, item];
    
    // Track array item toggles
    dataTracker.trackInteraction('toggle_array_item', item, {
      action: array.includes(item) ? 'remove' : 'add',
      array_type: 'preferences',
      current_array: array,
      item_toggled: item
    });
    
    return newArray;
  };

  const syncWithGlobalState = useCallback(() => {
    // Sync search filters with global state
    setSearchFilters({
      location: searchFilters.location || 'Austin, TX',
      priceRange: [0, userPreferences.budget || 2500] as [number, number],
      bedrooms: parseInt(preferences.bedrooms) || 1,
      amenities: preferences.amenities
    });

    // Sync user preferences with global state
    setUserPreferences({
      budget: userPreferences.budget || 2500,
      location: userPreferences.location || 'Austin, TX',
      moveInDate: userPreferences.moveInDate
    });
  }, [searchFilters, userPreferences, preferences, setSearchFilters, setUserPreferences]);

    // Auto-sync when key preferences change
  useEffect(() => {
    syncWithGlobalState();
  }, [preferences.amenities, preferences.bedrooms, syncWithGlobalState]);

  const handleSave = async () => {
    try {
      setSaving(true);
      
      // Sync with global state first
      syncWithGlobalState();

      // Save to Supabase
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        toast.error('Please sign in to save preferences');
        navigate('/auth');
        return;
      }

        /* eslint-disable-next-line @typescript-eslint/no-explicit-any */
        const { error } = await (supabase as any)
          .from('user_preferences')
          .upsert({
            user_id: user.id,
            email: user.email,
            bedrooms: preferences.bedrooms,
            amenities: preferences.amenities,
            deal_breakers: preferences.dealBreakers,
            
            // Transportation & Mobility
            public_transit_access: preferences.publicTransitAccess,
            walkability_score_requirement: preferences.walkabilityScoreRequirement,
            bike_friendly: preferences.bikeFriendly,
            ev_charging_stations: preferences.evChargingStations,
            ride_share_availability: preferences.rideShareAvailability,
            airport_proximity: preferences.airportProximity,
            highway_access: preferences.highwayAccess,
            
            // Neighborhood & Community
            school_district_quality: preferences.schoolDistrictQuality,
            crime_rate_preference: preferences.crimeRatePreference,
            noise_level_tolerance: preferences.noiseLevelTolerance,
            population_density: preferences.populationDensity,
            age_demographics: preferences.ageDemographics,
            diversity_index: preferences.diversityIndex,
            local_culture_arts: preferences.localCultureArts,
            
            // Safety & Security
            security_system_required: preferences.securitySystemRequired,
            gated_community_preference: preferences.gatedCommunityPreference,
            fire_safety_features: preferences.fireSafetyFeatures,
            
            // Shopping & Services
            grocery_store_types: preferences.groceryStoreTypes,
            shopping_mall_access: preferences.shoppingMallAccess,
            farmers_markets: preferences.farmersMarkets,
            banking_access: preferences.bankingAccess,
            post_office_proximity: preferences.postOfficeProximity,
            dry_cleaning_services: preferences.dryCleaningServices,
            
            // Technology & Connectivity
            internet_speed_requirement: preferences.internetSpeedRequirement,
            cell_tower_coverage: preferences.cellTowerCoverage,
            smart_home_compatibility: preferences.smartHomeCompatibility,
            
            lifestyle: preferences.lifestyle,
            work_schedule: preferences.workSchedule,
            priorities: preferences.priorities,
            bio: preferences.bio,
            use_case: preferences.useCase,
            additional_notes: preferences.additionalNotes,
            has_completed_ai_programming: true,
            ai_preferences: {
              amenityImportant: preferences.amenities.length > 3,
              transportationFocused: preferences.publicTransitAccess.length > 0 || preferences.bikeFriendly,
              safetyConscious: preferences.securitySystemRequired || preferences.crimeRatePreference === 'very-low',
              techSavvy: preferences.smartHomeCompatibility || preferences.internetSpeedRequirement === 'gigabit',
              lifestyle: preferences.lifestyle,
              priorities: preferences.priorities
            },
            search_criteria: {
              preferredAmenities: preferences.amenities,
              dealBreakers: preferences.dealBreakers,
              transportationNeeds: preferences.publicTransitAccess,
              safetyRequirements: preferences.fireSafetyFeatures,
              communityPreferences: {
                crimeRate: preferences.crimeRatePreference,
                noiseLevel: preferences.noiseLevelTolerance,
                demographics: preferences.ageDemographics
              }
            }
          });

      if (error) throw error;

      // Track successful save
      dataTracker.trackContent({
        contentType: 'ai_preferences',
        action: 'create',
        contentData: {
          preferences_count: Object.keys(preferences).length,
          amenities_selected: preferences.amenities.length,
          deal_breakers_selected: preferences.dealBreakers.length,
          priorities_selected: preferences.priorities.length,
          completion_time: Date.now(),
          has_bio: !!preferences.bio,
          has_use_case: !!preferences.useCase
        }
      });

      toast.success('AI preferences saved successfully!');
      navigate('/dashboard');
    } catch (error: unknown) {
      const message = error instanceof Error ? error.message : String(error);
      console.error('Error saving preferences:', message);

      // Track save error
      dataTracker.trackContent({
        contentType: 'ai_preferences',
        action: 'update',
        contentData: {
          error: message,
          step: 'save_failed',
          timestamp: new Date().toISOString()
        }
      });

      toast.error('Failed to save preferences: ' + message);
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className={`${designSystem.backgrounds.page} ${designSystem.backgrounds.pageDark}`}>
      <Header />
      
      <ModernPageLayout
        title="Program Your AI"
        subtitle="Customize your AI assistant to find the perfect rental with personalized preferences and smart filters"
        showHeader={false}
        headerContent={
          <Button 
            onClick={handleSave}
            disabled={saving}
            className={`${designSystem.buttons.primary} gap-2`}
          >
            <Target size={16} />
            {saving ? 'Saving...' : 'Save Preferences'}
          </Button>
        }
      >
        {/* AI Customization Overview */}
        <div className={`${designSystem.animations.entrance} mb-8`}>
          <ModernCard gradient className="text-center p-8">
            <div className="flex items-center justify-center gap-4 mb-6">
              <div className="w-16 h-16 rounded-full bg-gradient-to-br from-blue-600 to-purple-600 flex items-center justify-center">
                <Brain className="w-8 h-8 text-white" />
              </div>
              <div className="text-left">
                <h2 className={`${designSystem.typography.subheadingLarge} mb-1`}>
                  AI Personalization Engine
                </h2>
                <p className={designSystem.typography.body}>
                  Train your AI to understand your unique preferences and lifestyle needs
                </p>
              </div>
            </div>
            <p className={`${designSystem.typography.bodyLarge} max-w-3xl mx-auto`}>
              These settings sync with your dashboard and help our AI provide more accurate recommendations, 
              better negotiation strategies, and personalized market insights.
            </p>
          </ModernCard>
        </div>

        <div className="grid grid-cols-1 gap-6">
          {/* Housing */}
          <ModernCard 
            title="Housing Preferences"
            subtitle="Define your housing needs and must-have amenities"
            icon={<Home className="w-6 h-6 text-blue-600" />}
            animate
            className="mb-6"
          >
            <CardContent className="space-y-4">
              <div>
                <Label className="text-sm font-medium">Must-Have Amenities</Label>
                <div className="grid grid-cols-2 gap-2 mt-2">
                  {commonAmenities.map((amenity) => (
                    <div key={amenity} className="flex items-center space-x-2">
                      <Checkbox
                        id={`amenity-${amenity}`}
                        checked={preferences.amenities.includes(amenity)}
                        onCheckedChange={() => 
                          updatePreference('amenities', toggleArrayItem(preferences.amenities, amenity))
                        }
                      />
                      <Label htmlFor={`amenity-${amenity}`} className="text-xs">{amenity}</Label>
                    </div>
                  ))}
                </div>
              </div>

              <div>
                <Label className="text-sm font-medium">Deal Breakers</Label>
                <div className="grid grid-cols-2 gap-2 mt-2">
                  {commonDealBreakers.map((dealBreaker) => (
                    <div key={dealBreaker} className="flex items-center space-x-2">
                      <Checkbox
                        id={`dealbreaker-${dealBreaker}`}
                        checked={preferences.dealBreakers.includes(dealBreaker)}
                        onCheckedChange={() => 
                          updatePreference('dealBreakers', toggleArrayItem(preferences.dealBreakers, dealBreaker))
                        }
                      />
                      <Label htmlFor={`dealbreaker-${dealBreaker}`} className="text-xs">{dealBreaker}</Label>
                    </div>
                  ))}
                </div>
              </div>
            </CardContent>
          </ModernCard>
 
          {/* Transportation & Mobility */}
          <Card className="glass-dark border-border/20">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <MapPin className="w-5 h-5 text-blue-400" />
                Transportation & Mobility
              </CardTitle>
              <CardDescription>How you'll get around and commute preferences</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label className="text-sm font-medium">Public Transit Access</Label>
                <div className="grid grid-cols-2 gap-2 mt-2">
                  {['Subway', 'Bus', 'Light Rail', 'Commuter Train'].map((transit) => (
                    <div key={transit} className="flex items-center space-x-2">
                      <Checkbox
                        id={`transit-${transit}`}
                        checked={preferences.publicTransitAccess.includes(transit)}
                        onCheckedChange={() => 
                          updatePreference('publicTransitAccess', toggleArrayItem(preferences.publicTransitAccess, transit))
                        }
                      />
                      <Label htmlFor={`transit-${transit}`} className="text-xs">{transit}</Label>
                    </div>
                  ))}
                </div>
              </div>

              <div>
                <Label className="text-sm font-medium">Walkability Score Requirements</Label>
                <Select value={preferences.walkabilityScoreRequirement} onValueChange={(value) => updatePreference('walkabilityScoreRequirement', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="low">Low Priority (Any Score)</SelectItem>
                    <SelectItem value="moderate">Moderate (50+ Score)</SelectItem>
                    <SelectItem value="high">High Priority (70+ Score)</SelectItem>
                    <SelectItem value="essential">Essential (90+ Score)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Ride-Share Availability</Label>
                <Select value={preferences.rideShareAvailability} onValueChange={(value) => updatePreference('rideShareAvailability', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="not-important">Not Important</SelectItem>
                    <SelectItem value="standard">Standard Availability</SelectItem>
                    <SelectItem value="high">High Availability</SelectItem>
                    <SelectItem value="essential">Essential</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="bike-friendly"
                    checked={preferences.bikeFriendly}
                    onCheckedChange={(checked) => updatePreference('bikeFriendly', checked)}
                  />
                  <Label htmlFor="bike-friendly" className="text-sm">Bike-Friendly Infrastructure</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="ev-charging"
                    checked={preferences.evChargingStations}
                    onCheckedChange={(checked) => updatePreference('evChargingStations', checked)}
                  />
                  <Label htmlFor="ev-charging" className="text-sm">Car Charging Stations (EV)</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="highway-access"
                    checked={preferences.highwayAccess}
                    onCheckedChange={(checked) => updatePreference('highwayAccess', checked)}
                  />
                  <Label htmlFor="highway-access" className="text-sm">Highway Access</Label>
                </div>
              </div>

              <div>
                <Label className="text-sm font-medium">Airport Proximity</Label>
                <Select value={preferences.airportProximity} onValueChange={(value) => updatePreference('airportProximity', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="close">Close (0-30 min)</SelectItem>
                    <SelectItem value="moderate">Moderate (30-60 min)</SelectItem>
                    <SelectItem value="far">Far (60+ min acceptable)</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </CardContent>
          </Card>

          {/* Neighborhood & Community */}
          <Card className="glass-dark border-border/20">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Home className="w-5 h-5 text-green-400" />
                Neighborhood & Community
              </CardTitle>
              <CardDescription>Community characteristics and environment preferences</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label className="text-sm font-medium">School District Quality</Label>
                <Select value={preferences.schoolDistrictQuality} onValueChange={(value) => updatePreference('schoolDistrictQuality', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="no-preference">No Preference</SelectItem>
                    <SelectItem value="average">Average Schools OK</SelectItem>
                    <SelectItem value="good">Good Schools</SelectItem>
                    <SelectItem value="excellent">Excellent Schools Only</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Crime Rate Preferences</Label>
                <Select value={preferences.crimeRatePreference} onValueChange={(value) => updatePreference('crimeRatePreference', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="very-low">Very Low Crime</SelectItem>
                    <SelectItem value="low">Low Crime</SelectItem>
                    <SelectItem value="moderate">Moderate Crime Acceptable</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Noise Level Tolerance</Label>
                <Select value={preferences.noiseLevelTolerance} onValueChange={(value) => updatePreference('noiseLevelTolerance', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="very-quiet">Very Quiet</SelectItem>
                    <SelectItem value="quiet">Quiet</SelectItem>
                    <SelectItem value="moderate">Moderate Noise OK</SelectItem>
                    <SelectItem value="busy">Busy Area OK</SelectItem>
                    <SelectItem value="urban">Urban/City Noise OK</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Population Density</Label>
                <Select value={preferences.populationDensity} onValueChange={(value) => updatePreference('populationDensity', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="suburban">Suburban</SelectItem>
                    <SelectItem value="moderate">Moderate Density</SelectItem>
                    <SelectItem value="urban">Urban/High Density</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Age Demographics</Label>
                <Select value={preferences.ageDemographics} onValueChange={(value) => updatePreference('ageDemographics', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="young-professionals">Young Professionals</SelectItem>
                    <SelectItem value="families">Family-Oriented</SelectItem>
                    <SelectItem value="mixed">Mixed Ages</SelectItem>
                    <SelectItem value="mature">Mature Community</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Diversity Index</Label>
                <Select value={preferences.diversityIndex} onValueChange={(value) => updatePreference('diversityIndex', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="not-important">Not Important</SelectItem>
                    <SelectItem value="moderate">Moderate Diversity</SelectItem>
                    <SelectItem value="high">High Diversity Preferred</SelectItem>
                    <SelectItem value="essential">Essential</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="flex items-center space-x-2">
                <Checkbox
                  id="culture-arts"
                  checked={preferences.localCultureArts}
                  onCheckedChange={(checked) => updatePreference('localCultureArts', checked)}
                />
                <Label htmlFor="culture-arts" className="text-sm">Local Culture & Arts Scene Important</Label>
              </div>
            </CardContent>
          </Card>

          {/* Safety & Security */}
          <Card className="glass-dark border-border/20">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <MapPin className="w-5 h-5 text-red-400" />
                Safety & Security
              </CardTitle>
              <CardDescription>Security features and safety requirements</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">

              <div>
                <Label className="text-sm font-medium">Gated Community Preference</Label>
                <Select value={preferences.gatedCommunityPreference} onValueChange={(value) => updatePreference('gatedCommunityPreference', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="required">Required</SelectItem>
                    <SelectItem value="preferred">Preferred</SelectItem>
                    <SelectItem value="no-preference">No Preference</SelectItem>
                    <SelectItem value="avoid">Prefer to Avoid</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Fire Safety Features</Label>
                <div className="grid grid-cols-2 gap-2 mt-2">
                  {['Sprinkler System', 'Fire Extinguishers', 'Smoke Detectors', 'Fire Escape'].map((feature) => (
                    <div key={feature} className="flex items-center space-x-2">
                      <Checkbox
                        id={`fire-${feature}`}
                        checked={preferences.fireSafetyFeatures.includes(feature)}
                        onCheckedChange={() => 
                          updatePreference('fireSafetyFeatures', toggleArrayItem(preferences.fireSafetyFeatures, feature))
                        }
                      />
                      <Label htmlFor={`fire-${feature}`} className="text-xs">{feature}</Label>
                    </div>
                  ))}
                </div>
              </div>

              <div className="grid grid-cols-1 gap-4">
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="security-system"
                    checked={preferences.securitySystemRequired}
                    onCheckedChange={(checked) => updatePreference('securitySystemRequired', checked)}
                  />
                  <Label htmlFor="security-system" className="text-sm">Security System Required</Label>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Shopping & Services */}
          <Card className="glass-dark border-border/20">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <DollarSign className="w-5 h-5 text-green-400" />
                Shopping & Services
              </CardTitle>
              <CardDescription>Access to shopping and essential services</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label className="text-sm font-medium">Preferred Grocery Stores</Label>
                <div className="grid grid-cols-2 gap-2 mt-2">
                  {['Whole Foods', "Trader Joe's", 'Kroger', 'Safeway', 'Local Markets', "Costco/Sam's"].map((store) => (
                    <div key={store} className="flex items-center space-x-2">
                      <Checkbox
                        id={`grocery-${store}`}
                        checked={preferences.groceryStoreTypes.includes(store)}
                        onCheckedChange={() => 
                          updatePreference('groceryStoreTypes', toggleArrayItem(preferences.groceryStoreTypes, store))
                        }
                      />
                      <Label htmlFor={`grocery-${store}`} className="text-xs">{store}</Label>
                    </div>
                  ))}
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="shopping-mall"
                    checked={preferences.shoppingMallAccess}
                    onCheckedChange={(checked) => updatePreference('shoppingMallAccess', checked)}
                  />
                  <Label htmlFor="shopping-mall" className="text-sm">Shopping Mall Access</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="farmers-markets"
                    checked={preferences.farmersMarkets}
                    onCheckedChange={(checked) => updatePreference('farmersMarkets', checked)}
                  />
                  <Label htmlFor="farmers-markets" className="text-sm">Farmer's Markets</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="banking-access"
                    checked={preferences.bankingAccess}
                    onCheckedChange={(checked) => updatePreference('bankingAccess', checked)}
                  />
                  <Label htmlFor="banking-access" className="text-sm">Banking/ATM Access</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="post-office"
                    checked={preferences.postOfficeProximity}
                    onCheckedChange={(checked) => updatePreference('postOfficeProximity', checked)}
                  />
                  <Label htmlFor="post-office" className="text-sm">Post Office Proximity</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="dry-cleaning"
                    checked={preferences.dryCleaningServices}
                    onCheckedChange={(checked) => updatePreference('dryCleaningServices', checked)}
                  />
                  <Label htmlFor="dry-cleaning" className="text-sm">Dry Cleaning Services</Label>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Technology & Connectivity */}
          <Card className="glass-dark border-border/20">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Clock className="w-5 h-5 text-purple-400" />
                Technology & Connectivity
              </CardTitle>
              <CardDescription>Internet, technology, and connectivity requirements</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label className="text-sm font-medium">High-Speed Internet Availability</Label>
                <Select value={preferences.internetSpeedRequirement} onValueChange={(value) => updatePreference('internetSpeedRequirement', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="basic">Basic (25+ Mbps)</SelectItem>
                    <SelectItem value="standard">Standard (100+ Mbps)</SelectItem>
                    <SelectItem value="high-speed">High-Speed (500+ Mbps)</SelectItem>
                    <SelectItem value="gigabit">Gigabit (1000+ Mbps)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Cell Tower Coverage</Label>
                <Select value={preferences.cellTowerCoverage} onValueChange={(value) => updatePreference('cellTowerCoverage', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="basic">Basic Coverage</SelectItem>
                    <SelectItem value="good">Good Coverage</SelectItem>
                    <SelectItem value="excellent">Excellent Coverage</SelectItem>
                    <SelectItem value="5g-required">5G Required</SelectItem>
                  </SelectContent>
                </Select>
              </div>


              <div className="flex items-center space-x-2">
                <Checkbox
                  id="smart-home"
                  checked={preferences.smartHomeCompatibility}
                  onCheckedChange={(checked) => updatePreference('smartHomeCompatibility', checked)}
                />
                <Label htmlFor="smart-home" className="text-sm">Smart Home Compatibility</Label>
              </div>
            </CardContent>
          </Card>

          {/* Lifestyle & Priorities */}
          <Card className="glass-dark border-border/20">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Heart className="w-5 h-5 text-pink-400" />
                Lifestyle & Priorities
              </CardTitle>
              <CardDescription>Help AI understand your lifestyle</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label className="text-sm font-medium">Lifestyle</Label>
                <Select value={preferences.lifestyle} onValueChange={(value) => updatePreference('lifestyle', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue placeholder="Select your lifestyle" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="active">Active & Outdoorsy</SelectItem>
                    <SelectItem value="social">Social & Nightlife</SelectItem>
                    <SelectItem value="homebody">Homebody & Quiet</SelectItem>
                    <SelectItem value="professional">Career-Focused</SelectItem>
                    <SelectItem value="family">Family-Oriented</SelectItem>
                    <SelectItem value="creative">Creative & Artistic</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Work Schedule</Label>
                <Select value={preferences.workSchedule} onValueChange={(value) => updatePreference('workSchedule', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue placeholder="Select your work schedule" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="traditional">Traditional 9-5</SelectItem>
                    <SelectItem value="flexible">Flexible Hours</SelectItem>
                    <SelectItem value="remote">Remote Work</SelectItem>
                    <SelectItem value="hybrid">Hybrid Work</SelectItem>
                    <SelectItem value="shift">Shift Work</SelectItem>
                    <SelectItem value="freelance">Freelance/Gig</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Your Priorities</Label>
                <div className="grid grid-cols-2 gap-2 mt-2">
                  {priorityOptions.map((priority) => (
                    <div key={priority} className="flex items-center space-x-2">
                      <Checkbox
                        id={`priority-${priority}`}
                        checked={preferences.priorities.includes(priority)}
                        onCheckedChange={() => 
                          updatePreference('priorities', toggleArrayItem(preferences.priorities, priority))
                        }
                      />
                      <Label htmlFor={`priority-${priority}`} className="text-xs">{priority}</Label>
                    </div>
                  ))}
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Additional Information */}
          <Card className="glass-dark border-border/20">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Target className="w-5 h-5 text-yellow-400" />
                Additional Information
              </CardTitle>
              <CardDescription>Optional details to further personalize your AI</CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label className="text-sm font-medium">Bio / About You</Label>
                <Textarea
                  placeholder="Tell us about yourself..."
                  value={preferences.bio}
                  onChange={(e) => updatePreference('bio', e.target.value)}
                  className="mt-1 bg-slate-800/50 border-slate-600/50"
                  rows={3}
                />
              </div>

              <div>
                <Label className="text-sm font-medium">Primary Use Case</Label>
                <Select value={preferences.useCase} onValueChange={(value) => updatePreference('useCase', value)}>
                  <SelectTrigger className="mt-1 bg-slate-800/50 border-slate-600/50">
                    <SelectValue placeholder="What's your main reason for renting?" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="first-time">First-time Renter</SelectItem>
                    <SelectItem value="relocating">Relocating for Work</SelectItem>
                    <SelectItem value="downsizing">Downsizing</SelectItem>
                    <SelectItem value="upgrading">Upgrading Current Living</SelectItem>
                    <SelectItem value="temporary">Temporary Housing</SelectItem>
                    <SelectItem value="investment">Investment/Secondary</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label className="text-sm font-medium">Additional Notes</Label>
                <Textarea
                  placeholder="Any other preferences or requirements..."
                  value={preferences.additionalNotes}
                  onChange={(e) => updatePreference('additionalNotes', e.target.value)}
                  className="mt-1 bg-slate-800/50 border-slate-600/50"
                  rows={3}
                />
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Search Settings Section */}
        <div className="mt-8">
          <div className="mb-6">
            <h2 className={`${designSystem.typography.heading2} text-slate-900 mb-2`}>
              Search Settings
            </h2>
            <p className={`${designSystem.typography.body} text-slate-600`}>
              Configure your apartment search criteria and preferences
            </p>
          </div>
          
          <div className="max-w-2xl">
            <EnhancedSearchSettings
              onSettingsChange={setSearchSettings}
            />
          </div>
        </div>

        {/* Save Button */}
        <div className="mt-8 flex justify-center">
          <Button
            onClick={handleSave}
            disabled={saving}
            className="px-8 py-3 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700"
          >
            {saving ? 'Saving...' : 'Save AI Preferences & Search Settings'}
          </Button>
        </div>
      </ModernPageLayout>
    </div>
  );
};

export default ProgramAI;

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { 
  ArrowLeft, 
  MapPin, 
  Calendar, 
  Users, 
  Car, 
  Wifi, 
  Dumbbell, 
  Waves,
  Shield,
  Zap,
  TrendingUp,
  Clock,
  Heart,
  Share2,
  Phone,
  Mail,
  Star,
  Bed,
  Bath,
  Maximize,
  Building,
  DollarSign,
  AlertTriangle,
  Camera,
  Target,
  HelpCircle
} from 'lucide-react';
import Header from '@/components/Header';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { mockProperties, type Property } from '@/data/mockData';
import { usePropertyState } from '@/contexts';
import { ApartmentIQAI, SampleDataFactory } from '@/lib/apartmentiq-ai';
import { AlgorithmAnalyticsTracker } from '@/lib/analytics-architecture-audit';
import { toast } from '@/hooks/use-toast';
import { designSystem } from '@/lib/design-system';
import ModernPageLayout from '@/components/modern/ModernPageLayout';
import ModernCard from '@/components/modern/ModernCard';
import Breadcrumb from '@/components/Breadcrumb';

const PropertyDetails: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { 
    selectedProperty,
    setSelectedProperty,
    favoriteProperties,
    toggleFavorite 
  } = usePropertyState();
  
  const [property, setProperty] = useState<Property | null>(selectedProperty);
  type AiAnalysis = {
    opportunityScore?: number;
    tier?: string;
    successRate?: number; // 0..1
    recommendation?: string;
  } | null;

  const [aiAnalysis, setAiAnalysis] = useState<AiAnalysis>(null);
  const [loading, setLoading] = useState<boolean>(!selectedProperty);
  const [activeImageIndex, setActiveImageIndex] = useState(0);

  const isFavorited = property ? favoriteProperties.includes(property.id) : false;

  useEffect(() => {
    const loadPropertyDetails = async () => {
      // If we have selectedProperty from context, use it
      if (selectedProperty && selectedProperty.id === id) {
        setProperty(selectedProperty);
        setLoading(false);
      } else {
        setLoading(true);
        
        // Find property in mock data
        const foundProperty = mockProperties.find(p => p.id === id);
        if (!foundProperty) {
          navigate('/dashboard');
          return;
        }
        
        setProperty(foundProperty);
        setSelectedProperty(foundProperty);
        setLoading(false);
      }
      
      // AI analysis should run in a separate effect depending on `property` below
    };

    if (id) {
      loadPropertyDetails();
    }
  }, [id, navigate, selectedProperty, setSelectedProperty]);

  // Run AI analysis when we have a property (separate effect to satisfy exhaustive-deps)
  useEffect(() => {
    const runAiAnalysis = async () => {
      if (!property) return;

      try {
        const aiEngine = new ApartmentIQAI();
        const tracker = new AlgorithmAnalyticsTracker();

        const propertyData = SampleDataFactory.createPropertyData();
        propertyData.propertyId = property.id;
        propertyData.rent = property.originalPrice;
        propertyData.vacancyDuration = property.daysVacant;

        const marketData = SampleDataFactory.createMarketData();
        const behavioralData = SampleDataFactory.createBehavioralData();
        const tenantProfile = SampleDataFactory.createTenantProfile();

        const { result } = await tracker.trackOpportunityAnalysis(
          propertyData,
          marketData,
          behavioralData,
          tenantProfile,
          'user-123',
          'session-456'
        );

        // Narrow result to the expected shape before setting state
        let normalized: AiAnalysis = null;
        if (result && typeof result === 'object') {
          const obj = result as unknown as Record<string, unknown>;
          normalized = {
            opportunityScore: typeof obj.opportunityScore === 'number' ? (obj.opportunityScore as number) : undefined,
            tier: typeof obj.tier === 'string' ? (obj.tier as string) : undefined,
            successRate: typeof obj.successRate === 'number' ? (obj.successRate as number) : undefined,
            recommendation: typeof obj.recommendation === 'string' ? (obj.recommendation as string) : undefined,
          };
        }

        setAiAnalysis(normalized);
      } catch (error: unknown) {
        const message = error instanceof Error ? error.message : String(error);
        console.error('AI analysis failed:', message);
      }
    };

    void runAiAnalysis();
  }, [property]);

  const handleFavorite = () => {
    if (!property) return;
    toggleFavorite(property.id);
  };

  const handleShare = () => {
    navigator.clipboard.writeText(window.location.href);
    toast({
      title: "Link copied!",
      description: "Property link copied to clipboard"
    });
  };

  const handleGenerateOffer = () => {
    if (property) {
      setSelectedProperty(property);
      navigate('/generate-offer');
    }
  };

  if (loading) {
    return (
      <div className={`${designSystem.backgrounds.page} ${designSystem.backgrounds.pageDark}`}>
        <Header />
        <div className={`${designSystem.layouts.container} ${designSystem.layouts.header} flex items-center justify-center min-h-[400px]`}>
          <ModernCard className="text-center p-8">
            <div className="flex flex-col items-center space-y-4">
              <div className="w-16 h-16 rounded-full bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 flex items-center justify-center">
                <Building className="w-8 h-8 text-blue-600 animate-pulse" />
              </div>
              <h3 className={`${designSystem.typography.subheadingLarge} mb-2`}>
                Loading Property Details...
              </h3>
              <p className={designSystem.typography.body}>
                Analyzing property data and market conditions
              </p>
            </div>
          </ModernCard>
        </div>
      </div>
    );
  }

  if (!property) {
    return (
      <div className={`${designSystem.backgrounds.page} ${designSystem.backgrounds.pageDark}`}>
        <Header />
        <div className={`${designSystem.layouts.container} ${designSystem.layouts.header} flex items-center justify-center min-h-[400px]`}>
          <ModernCard className="text-center p-8">
            <div className="flex flex-col items-center space-y-4">
              <div className="w-16 h-16 rounded-full bg-gradient-to-br from-red-50 to-orange-50 dark:from-red-900/20 dark:to-orange-900/20 flex items-center justify-center">
                <AlertTriangle className="w-8 h-8 text-red-600" />
              </div>
              <h1 className={`${designSystem.typography.subheadingLarge} mb-4`}>
                Property Not Found
              </h1>
              <Button onClick={() => navigate('/dashboard')} className={designSystem.buttons.primary}>
                Back to Dashboard
              </Button>
            </div>
          </ModernCard>
        </div>
      </div>
    );
  }

  // AI Pricing computed values
  const aiPredicted = property.aiPrice;
  const potentialSavings = property.originalPrice - aiPredicted;
  const monthlyConcession = (() => {
    const monthly = property.concessions?.find(c => /\/mo/i.test(c.value));
    if (monthly) {
      const match = monthly.value.match(/\$?([\d,]+)/);
      if (match) return Number(match[1].replace(/,/g, ''));
    }
    return 0;
  })();
  const totalMonthlySavings = potentialSavings + monthlyConcession;
  const annualSavings = totalMonthlySavings * 12;

  const getAvailabilityColor = (type: string) => {
    switch (type) {
      case 'immediate': return 'bg-green-500';
      case 'soon': return 'bg-yellow-500';
      case 'waitlist': return 'bg-red-500';
      default: return 'bg-muted';
    }
  };

  const amenityIcons: Record<string, React.ElementType> = {
    'Pool': Waves,
    'Gym': Dumbbell,
    'Business Center': Wifi,
    'Clubhouse': Users,
    'Rooftop Deck': Building,
    'Bike Storage': Car,
    'Coffee Bar': Users
  };

  return (
    <div className={`${designSystem.backgrounds.page} ${designSystem.backgrounds.pageDark}`}>
      <Header />
      
      <main className={`${designSystem.layouts.container} ${designSystem.layouts.header}`}>
        <Breadcrumb />
        
        {/* Navigation & Actions */}
        <div className={`${designSystem.animations.entrance} flex items-center justify-between mb-8`}>
          <Button
            variant="ghost"
            onClick={() => navigate('/dashboard')}
            className="flex items-center gap-2"
          >
            <ArrowLeft size={16} />
            Back to Search
          </Button>
          
          <div className="flex items-center gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={handleFavorite}
              className={isFavorited ? "text-red-500 border-red-500 bg-red-50 dark:bg-red-900/20" : ""}
            >
              <Heart size={16} className={isFavorited ? "fill-current" : ""} />
            </Button>
            <Button variant="outline" size="sm" onClick={handleShare}>
              <Share2 size={16} />
            </Button>
            <Button 
              onClick={handleGenerateOffer}
              className={`${designSystem.buttons.primary} gap-2`}
            >
              <Zap size={16} />
              Generate AI Offer
            </Button>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-6">
            {/* Property Header */}
            <ModernCard 
              className={`${designSystem.animations.entrance}`}
              gradient
            >
              <div className="flex items-start justify-between mb-6">
                <div className="flex-1">
                  <h1 className={`${designSystem.typography.hero} mb-3`}>{property.name}</h1>
                  <div className="flex items-center text-muted-foreground mb-6">
                    <MapPin size={18} className="mr-2" />
                    <span className={designSystem.typography.bodyLarge}>
                      {property.address}, {property.city}, {property.state} {property.zip}
                    </span>
                  </div>
                  
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div className="flex items-center gap-2 p-3 bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 rounded-lg">
                      <Bed size={18} className="text-blue-600" />
                      <span className="font-medium">{property.bedrooms} bed</span>
                    </div>
                    <div className="flex items-center gap-2 p-3 bg-gradient-to-r from-green-50 to-emerald-50 dark:from-green-900/20 dark:to-emerald-900/20 rounded-lg">
                      <Bath size={18} className="text-green-600" />
                      <span className="font-medium">{property.bathrooms} bath</span>
                    </div>
                    <div className="flex items-center gap-2 p-3 bg-gradient-to-r from-purple-50 to-pink-50 dark:from-purple-900/20 dark:to-pink-900/20 rounded-lg">
                      <Maximize size={18} className="text-purple-600" />
                      <span className="font-medium">{property.sqft} sq ft</span>
                    </div>
                    <div className="flex items-center gap-2 p-3 bg-gradient-to-r from-orange-50 to-red-50 dark:from-orange-900/20 dark:to-red-900/20 rounded-lg">
                      <Building size={18} className="text-orange-600" />
                      <span className="font-medium">{property.yearBuilt}</span>
                    </div>
                  </div>
                </div>
                
                <div className="text-right ml-6">
                  <div className="flex items-center gap-2 mb-3">
                    <div className={`w-3 h-3 rounded-full ${getAvailabilityColor(property.availabilityType)}`}></div>
                    <span className="text-sm font-medium">{property.availability}</span>
                  </div>
                  <Badge className="bg-gradient-to-r from-blue-500 to-purple-500 text-white mb-4">
                    {property.matchScore}% Match
                  </Badge>
                </div>
              </div>

              {/* Pricing Analysis */}
              <div className="bg-gradient-to-r from-green-50 to-emerald-50 dark:from-green-900/20 dark:to-emerald-900/20 rounded-xl p-6 border border-green-200 dark:border-green-800">
                <div className="flex items-center gap-3 mb-6">
                  <div className="p-3 rounded-full bg-green-100 dark:bg-green-900/30">
                    <DollarSign className="w-6 h-6 text-green-600" />
                  </div>
                  <div>
                    <h3 className={`${designSystem.typography.subheadingLarge} text-green-800 dark:text-green-400`}>
                      AI Pricing Analysis
                    </h3>
                    <p className="text-sm text-green-700 dark:text-green-500">
                      Based on market data and negotiation opportunities
                    </p>
                  </div>
                </div>

                <div className="grid grid-cols-3 gap-4 mb-6">
                  <div className="text-center p-4 bg-white/50 dark:bg-gray-800/50 rounded-lg">
                    <div className="text-xs text-muted-foreground mb-1">Original Price</div>
                    <div className="text-lg font-semibold text-muted-foreground line-through">
                      ${property.originalPrice.toLocaleString()}/mo
                    </div>
                  </div>
                  <div className="text-center p-4 bg-white/50 dark:bg-gray-800/50 rounded-lg">
                    <div className="text-xs text-muted-foreground mb-1">AI Predicted</div>
                    <div className="text-lg font-semibold text-blue-600">
                      ${aiPredicted.toLocaleString()}/mo
                    </div>
                  </div>
                  <div className="text-center p-4 bg-white/50 dark:bg-gray-800/50 rounded-lg">
                    <div className="text-xs text-muted-foreground mb-1">Your Savings</div>
                    <div className="text-lg font-semibold text-green-600">
                      ${potentialSavings.toLocaleString()}/mo
                    </div>
                  </div>
                </div>

                {/* Concession Value Line Item */}
                <div className="flex items-center justify-center mb-2">
                  <div className="text-sm">
                    <span className="text-muted-foreground">Concession Value: </span>
                    <span className="font-semibold text-green-600">${monthlyConcession.toLocaleString()}/mo</span>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4 pt-4 border-t border-green-200 dark:border-green-800">
                  <div className="text-center">
                    <div className="text-xs text-muted-foreground mb-1">Monthly Savings</div>
                    <div className="text-2xl font-bold text-green-600">
                      ${totalMonthlySavings.toLocaleString()}
                    </div>
                  </div>
                  <div className="text-center">
                    <div className="text-xs text-muted-foreground mb-1">Annual Savings</div>
                    <div className="text-2xl font-bold text-green-600">
                      ${annualSavings.toLocaleString()}
                    </div>
                  </div>
                </div>
              </div>
            </ModernCard>

            {/* Image Gallery */}
            <ModernCard 
              title="Property Photos"
              icon={<Camera className="w-6 h-6 text-blue-600" />}
              animate
              animationDelay={100}
              className="mb-6"
            >
              <div className="space-y-4">
                <div className="aspect-video rounded-lg bg-gradient-to-br from-gray-100 to-gray-200 dark:from-gray-800 dark:to-gray-700 flex items-center justify-center border-2 border-dashed border-gray-300 dark:border-gray-600">
                  <div className="text-center">
                    <Building size={48} className="mx-auto mb-2 text-muted-foreground" />
                    <p className="text-muted-foreground font-medium">Property Photos</p>
                    <p className="text-sm text-muted-foreground">Click to view full gallery</p>
                  </div>
                </div>
                <div className="grid grid-cols-4 gap-2">
                  {[1, 2, 3, 4].map((_, index) => (
                    <div 
                      key={index} 
                      className="aspect-square rounded-lg bg-gradient-to-br from-gray-100 to-gray-200 dark:from-gray-800 dark:to-gray-700 cursor-pointer hover:scale-105 transition-transform duration-200 border border-gray-200 dark:border-gray-600"
                    />
                  ))}
                </div>
              </div>
            </ModernCard>

            {/* Detailed Information Tabs */}
            <ModernCard 
              animate
              animationDelay={200}
              className="mb-6"
            >
              <Tabs defaultValue="features" className="w-full">
                <TabsList className={`grid w-full grid-cols-4 ${designSystem.backgrounds.card}`}>
                  <TabsTrigger value="features" className="gap-2">
                    <Shield size={16} />
                    Features
                  </TabsTrigger>
                  <TabsTrigger value="amenities" className="gap-2">
                    <Dumbbell size={16} />
                    Amenities
                  </TabsTrigger>
                  <TabsTrigger value="commute" className="gap-2">
                    <MapPin size={16} />
                    Commute
                  </TabsTrigger>
                  <TabsTrigger value="policies" className="gap-2">
                    <Users size={16} />
                    Policies
                  </TabsTrigger>
                </TabsList>
                
                <TabsContent value="features" className="space-y-4 mt-6">
                  <h4 className={`${designSystem.typography.subheadingLarge} mb-4`}>Property Features</h4>
                  <div className="grid grid-cols-2 gap-3">
                    {property.features.map((feature, index) => (
                      <div key={index} className="flex items-center gap-2 p-3 bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 rounded-lg">
                        <Shield size={16} className="text-blue-600" />
                        <span>{feature}</span>
                      </div>
                    ))}
                  </div>
                </TabsContent>
                
                <TabsContent value="amenities" className="space-y-4 mt-6">
                  <h4 className={`${designSystem.typography.subheadingLarge} mb-4`}>Building Amenities</h4>
                  <div className="grid grid-cols-2 gap-3">
                    {property.amenities.map((amenity, index) => {
                      const IconComponent = amenityIcons[amenity] || Users;
                      return (
                        <div key={index} className="flex items-center gap-2 p-3 bg-gradient-to-r from-green-50 to-emerald-50 dark:from-green-900/20 dark:to-emerald-900/20 rounded-lg">
                          <IconComponent size={16} className="text-green-600" />
                          <span>{amenity}</span>
                        </div>
                      );
                    })}
                  </div>
                </TabsContent>
                
                <TabsContent value="commute" className="space-y-4 mt-6">
                  <h4 className={`${designSystem.typography.subheadingLarge} mb-4`}>Commute Times</h4>
                  <div className="space-y-3">
                    {property.commutes.map((commute, index) => (
                      <div key={index} className="flex items-center justify-between p-3 bg-gradient-to-r from-purple-50 to-pink-50 dark:from-purple-900/20 dark:to-pink-900/20 rounded-lg">
                        <div className="flex items-center gap-2">
                          <MapPin size={16} className="text-purple-600" />
                          <span className="font-medium">{commute.location}</span>
                        </div>
                        <div className="flex items-center gap-2 text-sm font-medium">
                          <Clock size={14} className="text-purple-600" />
                          {commute.time} by {commute.method}
                        </div>
                      </div>
                    ))}
                  </div>
                </TabsContent>
                
                <TabsContent value="policies" className="space-y-4 mt-6">
                  <h4 className={`${designSystem.typography.subheadingLarge} mb-4`}>Property Policies</h4>
                  <div className="space-y-3">
                    <div className="p-4 bg-gradient-to-r from-orange-50 to-red-50 dark:from-orange-900/20 dark:to-red-900/20 rounded-lg">
                      <div className="font-medium mb-2 flex items-center gap-2">
                        <Users size={16} className="text-orange-600" />
                        Pet Policy
                      </div>
                      <div className="text-sm text-muted-foreground">{property.petPolicy}</div>
                    </div>
                    <div className="p-4 bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 rounded-lg">
                      <div className="font-medium mb-2 flex items-center gap-2">
                        <Car size={16} className="text-blue-600" />
                        Parking
                      </div>
                      <div className="text-sm text-muted-foreground">{property.parking}</div>
                    </div>
                  </div>
                </TabsContent>
              </Tabs>
            </ModernCard>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Landlord Loss Indicator */}
            <ModernCard 
              title="Landlord Loss Indicator"
              subtitle="If unit stays vacant"
              icon={<AlertTriangle className="w-6 h-6 text-amber-600" />}
              animate
              className="bg-gradient-to-br from-amber-50 to-orange-50 dark:from-amber-900/20 dark:to-orange-900/20 border border-amber-200 dark:border-amber-800"
            >
              <div className="grid grid-cols-3 gap-3">
                <div className="text-center p-3 bg-amber-100 dark:bg-amber-900/30 rounded-lg">
                  <div className="text-lg font-bold text-amber-600">$1,320</div>
                  <div className="text-xs text-muted-foreground mt-1">30 Days</div>
                </div>
                <div className="text-center p-3 bg-orange-100 dark:bg-orange-900/30 rounded-lg">
                  <div className="text-lg font-bold text-orange-600">$2,640</div>
                  <div className="text-xs text-muted-foreground mt-1">60 Days</div>
                </div>
                <div className="text-center p-3 bg-red-100 dark:bg-red-900/30 rounded-lg">
                  <div className="text-lg font-bold text-red-600">$3,960</div>
                  <div className="text-xs text-muted-foreground mt-1">90 Days</div>
                </div>
              </div>
            </ModernCard>
              
            {/* AI Analysis */}
            {aiAnalysis && (
              <ModernCard 
                title="AI Opportunity Analysis"
                icon={<Target className="w-6 h-6 text-blue-600" />}
                animate
                animationDelay={100}
                className="bg-gradient-to-br from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20"
              >
                <div className="text-center mb-6">
                  <div className="text-4xl font-bold text-blue-600 mb-2">
                    {Math.round(aiAnalysis.opportunityScore || 0)}%
                  </div>
                  <div className="text-sm text-muted-foreground mb-3">Opportunity Score</div>
                  <Badge className="bg-gradient-to-r from-blue-500 to-purple-500 text-white">
                    {aiAnalysis.tier}
                  </Badge>
                </div>
                
                <div className="space-y-3">
                  <div className="flex justify-between text-sm">
                    <span>Success Rate</span>
                    <span className="font-medium">{Math.round((aiAnalysis.successRate || 0) * 100)}%</span>
                  </div>
                  <Progress value={(aiAnalysis.successRate || 0) * 100} className="h-3" />
                </div>
                
                <div className="mt-4 p-4 bg-blue-100 dark:bg-blue-900/30 rounded-lg">
